$.fn.minimalTabs=function(){var id="#"+this.attr("id");var tabs=id+" p.tabs";var content=id+" ul.tabs-content";$(content+" .tab-content").hide();$(content+" .tab-content:first-child").show();$(tabs+" a").click(function(){$(tabs+" a").removeClass("open");$(this).addClass("open");$(content+" .tab-content").hide();$(content+" "+$(this).attr("href")).show();return false})};
